import React, { Fragment } from 'react';
import AppBar from '@material-ui/core/AppBar';
import { Button, Grid } from '@material-ui/core';
import { makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import FormControl from '@material-ui/core/FormControl';
import './dashboard.css';

const useStyles = makeStyles((theme) => ({
    
    submit: {
      width: '120px',
      height: '40px',
      color: 'black',
      float: 'right',
      fontSize: '18px',
      fontWeight: 'bolder',
      borderRadius: 15,
    },

    bar: {
        position: 'fixed',
        height: '10%',
        background: '#F5F5F5',
        textAlign: 'center',
        color: 'black',
        fontSize: '18px',
        fontWeight: 'bolder',
        margin: ''
    },

    formControl: {
        margin: theme.spacing(1),
        width: 200,
        height: 45,
    },

    input: {
        width: 200,
        height: 60,
    },

    button: {
        height: 40,
        width: 80,
        fontSize: 'bolder'
    }
}));


const Dashboard = () => {

    const classes = useStyles();

    return(
        <Fragment>
            <AppBar className={classes.bar}>
                <Grid container direction="row" alignItems="center">
                    <h1>My GPA</h1>
                </Grid>
            </AppBar>
            
            <div className="form">
                <center>
                    <div className="box tcase">
                        <h2>GPA</h2>
                    </div>
                    <div className="box tcase">
                        <h2>Class</h2>
                    </div>
                    <div className="box tcase">
                        <h2>Credits</h2>
                    </div>
                </center>
            </div>
            
            <div className="input_form">
                <form>
                    <center>
                        <TextField 
                            className={classes.input}
                            required 
                            name="subject"
                            id="subject" 
                            label="Required" 
                            defaultValue="SCSxxxx" 
                        />

                        <FormControl className={classes.formControl}>
                            <InputLabel id="year">Year</InputLabel>
                            <Select
                                labelId="year"
                                id="year"
                                name="year"
                                //value={age}
                                //onChange={handleChange}
                                >
                                <MenuItem value={1}>Firt</MenuItem>
                                <MenuItem value={2}>Second</MenuItem>
                                <MenuItem value={3}>Third</MenuItem>
                                <MenuItem value={4}>Fourth</MenuItem>
                            </Select>
                        </FormControl>

                        <FormControl className={classes.formControl}>
                            <InputLabel id="semester">Semester</InputLabel>
                            <Select
                                labelId="semester"
                                id="semester"
                                name="semester"
                                //value={age}
                                //onChange={handleChange}
                                >
                                <MenuItem value={1}>Firt</MenuItem>
                                <MenuItem value={2}>Second</MenuItem>
                            </Select>
                        </FormControl>

                        <FormControl className={classes.formControl}>
                            <InputLabel id="credit">Credit</InputLabel>
                            <Select
                                labelId="credit"
                                id="credit"
                                name="credit"
                                //value={age}
                                //onChange={handleChange}
                                >
                                <MenuItem value={1}>1</MenuItem>
                                <MenuItem value={2}>2</MenuItem>
                                <MenuItem value={3}>3</MenuItem>
                                <MenuItem value={4}>4</MenuItem>
                            </Select>
                        </FormControl>

                        <FormControl className={classes.formControl}>
                            <InputLabel id="grade">Grade</InputLabel>
                            <Select
                                labelId="grade"
                                id="grade"
                                name="grade"
                                //value={age}
                                //onChange={handleChange}
                                >
                                <MenuItem value={4.00}>A+</MenuItem>
                                <MenuItem value={4.00}>A</MenuItem>
                                <MenuItem value={3.70}>A-</MenuItem>
                                <MenuItem value={3.30}>B+</MenuItem>
                                <MenuItem value={3.00}>B</MenuItem>
                                <MenuItem value={2.70}>B-</MenuItem>
                                <MenuItem value={2.30}>C+</MenuItem>
                                <MenuItem value={2.00}>C</MenuItem>
                                <MenuItem value={1.70}>C-</MenuItem>
                                <MenuItem value={1.30}>D+</MenuItem>
                                <MenuItem value={1.00}>D</MenuItem>
                                <MenuItem value={0.00}>E</MenuItem>
                                <MenuItem value={0.00}>F</MenuItem>
                            </Select>
                        </FormControl>

                        <Button variant="contained" color="primary" className={classes.button} style={{ fontSize: '30px' }}>
                            Add
                        </Button>
                    </center>
                </form>
            </div>
            
        </Fragment>
        
    );
};

export default Dashboard;
