import React, { Fragment , useState, useEffect} from 'react';
import AppBar from '@material-ui/core/AppBar';
import { Button, Grid } from '@material-ui/core';
import { withStyles, makeStyles } from '@material-ui/core/styles';
import TextField from '@material-ui/core/TextField';
import InputLabel from '@material-ui/core/InputLabel';
import MenuItem from '@material-ui/core/MenuItem';
import Select from '@material-ui/core/Select';
import FormControl from '@material-ui/core/FormControl';
import Paper from '@material-ui/core/Paper';
import Table from '@material-ui/core/Table';
import TableBody from '@material-ui/core/TableBody';
import TableCell from '@material-ui/core/TableCell';
import TableContainer from '@material-ui/core/TableContainer';
import TableHead from '@material-ui/core/TableHead';
import TablePagination from '@material-ui/core/TablePagination';
import TableRow from '@material-ui/core/TableRow';
import './dashboard.css';

const StyledTableCell = withStyles((theme) => ({
    head: {
      backgroundColor: theme.palette.common.black,
      color: theme.palette.common.white,
    },
    body: {
      fontSize: 14,
    },
}))(TableCell);
  
const StyledTableRow = withStyles((theme) => ({
    root: {
      '&:nth-of-type(odd)': {
        backgroundColor: theme.palette.action.hover,
      },
    },
}))(TableRow);

const useStyles = makeStyles((theme) => ({
    
    submit: {
      width: '120px',
      height: '40px',
      color: 'black',
      float: 'right',
      fontSize: '18px',
      fontWeight: 'bolder',
      borderRadius: 15,
    },

    bar: {
        position: 'fixed',
        height: '10%',
        background: '#F5F5F5',
        textAlign: 'center',
        color: 'black',
        fontSize: '18px',
        fontWeight: 'bolder',
        margin: ''
    },

    formControl: {
        margin: theme.spacing(1),
        width: 200,
        height: 45,
    },

    input: {
        width: 200,
        height: 60,
    },

    button: {
        marginTop: 10,
        marginLeft: 15,
        height: 40,
        width: 70,
        fontSize: '18px',
        fontWeight: 'bold',
        borderRadius: 15,
    },

    table: {
        minWidth: 600,
    },

    cell: {
          width: 200,
    },

}));


const Dashboard = () => {

    const [inputs, setInputs] = useState({
        subject: "",
    });

    const [year, setYear] = useState({});
    const [semester, setSemester] = useState({});
    const [credit, setCredit] = useState({});
    const [grade, setGrade] = useState({});

    const {subject} = inputs;

    const onChange = (e) => {
        setInputs({...inputs, [e.target.name]
        : e.target.value})
    };

    const yearChange = (event) => {
        setYear(event.target.value);
    };

    const semesterChange = (event) => {
        setSemester(event.target.value);
    };

    const creditChange = (event) => {
        setCredit(event.target.value);
    };

    const gradeChange = (event) => {
        setGrade(event.target.value);
    };

    

    const classes = useStyles();

    return(
        <Fragment>
            <AppBar className={classes.bar}>
                <Grid container direction="row" alignItems="center">
                    <h1>My GPA</h1>
                </Grid>
            </AppBar>
            
            <div className="form">
                <center>
                    <div className="box tcase">
                        <h2>GPA</h2>
                    </div>
                    <div className="box tcase">
                        <h2>Class</h2>
                    </div>
                    <div className="box tcase">
                        <h2>Credits</h2>
                    </div>
                </center>
            </div>
            
            <div className="input_form">
                <form onSubmit={onsubmit}>
                    <center>
                        <TextField 
                            className={classes.input}
                            required 
                            name="subject"
                            id="subject" 
                            label="Required" 
                            onChange = {e => onChange(e)}
                            //defaultValue="SCSxxxx"
                            placeholder="SCSxxx" 
                        />

                        <FormControl className={classes.formControl}>
                            <InputLabel id="year">Year</InputLabel>
                            <Select
                                labelId="year"
                                id="year"
                                name="year"
                                //value={age}
                                onChange={yearChange}
                                >
                                <MenuItem value={1}>First</MenuItem>
                                <MenuItem value={2}>Second</MenuItem>
                                <MenuItem value={3}>Third</MenuItem>
                                <MenuItem value={4}>Fourth</MenuItem>
                            </Select>
                        </FormControl>

                        <FormControl className={classes.formControl}>
                            <InputLabel id="semester">Semester</InputLabel>
                            <Select
                                labelId="semester"
                                id="semester"
                                name="semester"
                                //value={age}
                                onChange={semesterChange}
                                >
                                <MenuItem value={1}>First</MenuItem>
                                <MenuItem value={2}>Second</MenuItem>
                            </Select>
                        </FormControl>

                        <FormControl className={classes.formControl}>
                            <InputLabel id="credit">Credit</InputLabel>
                            <Select
                                labelId="credit"
                                id="credit"
                                name="credit"
                                //value={age}
                                onChange={creditChange}
                                >
                                <MenuItem value={1}>1</MenuItem>
                                <MenuItem value={2}>2</MenuItem>
                                <MenuItem value={3}>3</MenuItem>
                                <MenuItem value={4}>4</MenuItem>
                            </Select>
                        </FormControl>

                        <FormControl className={classes.formControl}>
                            <InputLabel id="grade">Grade</InputLabel>
                            <Select
                                labelId="grade"
                                id="grade"
                                name="grade"
                                //value={age}
                                onChange={gradeChange}
                                >
                                <MenuItem value={4.00}>A+</MenuItem>
                                <MenuItem value={4.00}>A</MenuItem>
                                <MenuItem value={3.70}>A-</MenuItem>
                                <MenuItem value={3.30}>B+</MenuItem>
                                <MenuItem value={3.00}>B</MenuItem>
                                <MenuItem value={2.70}>B-</MenuItem>
                                <MenuItem value={2.30}>C+</MenuItem>
                                <MenuItem value={2.00}>C</MenuItem>
                                <MenuItem value={1.70}>C-</MenuItem>
                                <MenuItem value={1.30}>D+</MenuItem>
                                <MenuItem value={1.00}>D</MenuItem>
                                <MenuItem value={0.00}>E</MenuItem>
                                <MenuItem value={0.00}>F</MenuItem>
                            </Select>
                        </FormControl>

                        <Button variant="contained" color="primary" className={classes.button} >
                            Add
                        </Button>
                    </center>
                </form>
            </div>

            <div className="data-table">
                <TableContainer component={Paper}>
                    <Table className={classes.table} aria-label="customized table">
                        <TableHead>
                            <TableRow>
                                <StyledTableCell className={classes.cell} align="center">Subject Code</StyledTableCell>
                                <StyledTableCell className={classes.cell} align="center">Credit</StyledTableCell>
                                <StyledTableCell className={classes.cell} align="center">Grade</StyledTableCell>
                            </TableRow>
                        </TableHead>
                    </Table>
                </TableContainer>
            </div>
            
        </Fragment>
        
    );
};

export default Dashboard;
